import React, { useState, useEffect } from 'react';
import CategoryName from './CategoryName';
import axios from 'axios';

const ProjectCard = ({ project }) => {
  const [expanded, setExpanded] = useState(false);
  const [achievements, setAchievements] = useState([]);
  const [loading, setLoading] = useState(false);

  const toggleExpanded = () => {
    setExpanded(!expanded);
    if (!expanded) {
      // When expanding, fetch achievements for the project
      setLoading(true);
      axios
        .get(`/api/achievements/${project.Id}`)
        .then((response) => {
          setAchievements(response.Name);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching achievements:', error);
          setLoading(false);
        });
    }
  };

  return (
    <div className={`project-card ${expanded ? 'expanded' : ''}`}>
      <img src={project.ImageLink} alt={project.Name} />
      <h3>{project.Name}</h3>
      <p>Description: {project.Description}</p>
      <button onClick={toggleExpanded}>
        {expanded ? 'Hide Achievements' : 'Show Achievements'}
      </button>
      {expanded && (
        <div>
          {loading ? (
            <p>Loading achievements...</p>
          ) : (
            <ul>
              {achievements.map((achievement, index) => (
                <li key={index}>{achievement}</li>
              ))};
            </ul>
          )};
        </div>
      )};
    </div>
  );
};

export default ProjectCard;
