import React from 'react';

// Sample hardcoded achievements data for demonstration
const achievementsData = {
  1: [
    { Id: 1, Name: 'Reduced customer service time from 40 minutes to 20 minutes' },
    { Id: 2, Name: 'Decreased delay periods from 1 hour to 10 minutes' },
    { Id: 3, Name: 'Automated various manual processes' },
    { Id: 4, Name: 'Increased the capacity and efficiency of employees' },

    // Add more achievements as needed for project with Id 1
  ],
  2: [
    { Id: 3, Name: 'Achievement 3' },
    { Id: 4, Name: 'Achievement 4' },
    // Add more achievements as needed for project with Id 2
  ],

};

const ProjectCard = ({ project }) => {
  // Retrieve achievements based on the project Id
  const projectAchievements = achievementsData[project.Id] || [];

  return (
    <div className='project-card'>
      <div className="image-container">
        <a href={project.Links} target="_blank" rel="noopener noreferrer">
          <img src={project.ImageLink} alt={project.Name} />
        </a>
      </div>

      <div className="content-container">
        <h3>{project.Name}</h3>
        <h4>Problem Statement:</h4>
        <p>{project.Description}</p>
        <h4>Tools/Libraries:</h4>
        <p>{project.Technics}</p>
        <h4>Skills:</h4>
        <p>{project.Skills}</p>
        <h4>Achievements:</h4>
        <ul>
          {projectAchievements.map((achievement) => (
            <li key={achievement.Id}>{achievement.Name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ProjectCard;
