import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectCard from './components/ProjectCard';
import CategoryName from './components/CategoryName';
import './portfolio.css';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const apiUrl = process.env.REACT_APP_API_URL;
  const [categoryNames, setCategoryNames] = useState({});
  const [currentCategory, setCurrentCategory] = useState(0);
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);

  useEffect(() => {
    axios
      .get(`${apiUrl}/api/projects`)
      .then((response) => {
        setProjects(response.data);
      })
      .catch((error) => {
        console.error('Error retrieving projects:', error);
      });

    axios
      .get(`${apiUrl}/api/categories`)
      .then((response) => {
        const categoryData = {};
        response.data.forEach((category) => {
          categoryData[category.Id] = category.Name;
        });
        setCategoryNames(categoryData);
      })
      .catch((error) => {
        console.error('Error retrieving categories:', error);
      });
  }, []);

  const categories = Object.keys(categoryNames);

  const handlePreviousProject = () => {
    if (currentProjectIndex > 0) {
      setCurrentProjectIndex(currentProjectIndex - 1);
    } else if (currentCategory > 0) {
      setCurrentCategory(currentCategory - 1);
      setCurrentProjectIndex(
        projects
          .filter((project) => project.CategoryId === categories[currentCategory - 1])
          .length - 1
      );
    }
  };

  const handleNextProject = () => {
    if (currentProjectIndex < projects.length - 1) {
      setCurrentProjectIndex(currentProjectIndex + 1);
    } else if (currentCategory < categories.length - 1) {
      setCurrentCategory(currentCategory + 1);
      setCurrentProjectIndex(0);
    }
  };

  return (
    <div className="portfolio">
      <div className="category-container">
        <h2>
          <CategoryName Id={categories[currentCategory]} /> Projects
        </h2>
        <div className="project-list">
          <ProjectCard key={projects[currentProjectIndex].Id} project={projects[currentProjectIndex]} />
        </div>
        <div className="navigation-buttons">
          <button onClick={handlePreviousProject}>Previous</button>
          <button onClick={handleNextProject}>Next</button>
        </div>
      </div>
    </div>
  );
}

export default Portfolio;
