import React, { useState, useEffect } from 'react';
import CategoryName from './CategoryName';
import axios from 'axios';

const apiUrl = process.env.REACT_APP_API_URL;

const ProjectCard = ({ project }) => {
  const [expanded, setExpanded] = useState(false); // Set expanded to false
  const [achievements, setAchievements] = useState([]);
  const [loading, setLoading] = useState(false);

  const toggleExpanded = () => {
    setExpanded(!expanded); // Toggle the expanded state
    if (expanded) {
      setLoading(true);
      axios
        .get(`${apiUrl}/api/achievements/${project.Id}`)
        .then((response) => {
          setAchievements(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching achievements:', error);
          setLoading(false);
        });
    }
  };

  return (
    <div className={`project-card ${expanded ? 'expanded' : ''}`}>
      <h3 onClick={toggleExpanded}>{project.Name}</h3>
      <a href={project.GitHubLink} target="_blank" rel="noopener noreferrer">

      <img onClick={toggleExpanded} src={project.ImageLink} alt={project.Name} />
      </a>

      {expanded && (
        <div>
          <h4>Problem Statement:</h4>
          <p>{project.Description}</p>
          <h4>Tools/Libraries:</h4>
          <p>{project.Technics}</p>
          <h4>Skills:</h4>
          <p>{project.Skills}</p>
          <h4>Achievements:</h4>
          {loading ? (
            <p>Loading achievements...</p>
          ) : (
            <ul>
              {achievements.map((achievement, index) => (
                <li key={index}>{achievement.Name}</li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
};

export default ProjectCard;
