import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faChartBar,faHdd,faCloud,} from '@fortawesome/free-solid-svg-icons'; // Icons you need
import { faHtml5, faCss3 } from '@fortawesome/free-solid-svg-icons';
import { faDatabase } from '@fortawesome/free-solid-svg-icons';






function Skills() {
  const skillGroups = [
    {
      group: 'Programming Languages',
      items: [
        { name: 'Python', icon: PythonIcon, color: 'blue' },
        { name: 'SQL (PostgreSQL, MySQL)', icon: faDatabase, color: 'orange' },
        { name: 'Python Libraries (numpy, pandas, scikit-learn, streamlit, matplotlib, Pytorch, Seaborn, ONNX, Sqlalchemy)', icon: PythonIcon, color: 'green' },
        { name: 'Javascript', color: 'yellow' },
        { name: 'HTML', icon: faHtml5, color: 'red' },
        { name: 'CSS', icon: faCss3, color: 'teal' },
      ],
    },
    {
      group: 'Machine Learning (ML)',
      items: [
        { name: 'Linear and Logistic Regression', icon: faChartBar, color: 'blue' },
        { name: 'Random Forests', icon: faChartBar, color: 'green' },
        { name: 'PCA', icon: faChartBar, color: 'orange' },
        { name: 'Gradient Boosting', icon: faChartBar, color: 'purple' },
        { name: 'K-means', icon: faChartBar, color: 'teal' },
        { name: 'Deep Learning', icon: faChartBar, color: 'red' },
        { name: 'NLP (nltk, SpaCy, Word2Vec, GloVe, TF-IDF)', icon: faChartBar, color: 'blue' },
        { name: 'Time Series Forecasting (Facebook Prophet, SARIMA models)', icon: faChartBar, color: 'green' },
        { name: 'PyTorch', icon: faChartBar, color: 'orange' },
        { name: 'TensorFlow 2.0', icon: faChartBar, color: 'purple' },
        { name: 'HuggingFace', icon: faChartBar, color: 'teal' },
        { name: 'PyCaret', icon: faChartBar, color: 'red' },
      ],
    },
    {
      group: 'Data',
      items: [
        { name: 'Data Visualization (Tableau, AWS QuickSight, Vizro, Atoti)', icon: faChartBar, color: 'blue' },
        { name: 'Processing Large Data Sets', icon: faHdd, color: 'orange' },
        { name: 'A/B testing', icon: faChartBar, color: 'purple' },
        { name: 'Experimentation', icon: faChartBar, color: 'teal' },
        { name: 'Distributed Computing (PySpark)', icon: faChartBar, color: 'green' },
        { name: 'Product Analytics', icon: faChartBar, color: 'red' },
      ],
    },
    {
      group: 'Cloud Computing',
      items: [
        { name: 'Amazon Web Services (AWS)', icon: faCloud, color: 'orange' },
        { name: 'EMR, EC2, S3', icon: faCloud, color: 'blue' },
        { name: 'SageMaker, AWS Glue, BigQuery', icon: faCloud, color: 'purple' },
        { name: 'Docker, Docker Compose, Git', icon: faCloud, color: 'green' },
        { name: 'Model Orchestration (Mage AI)', icon: faCloud, color: 'teal' },
      ],
    },
  ];

  return (
    <div className='skills'>
      <h3>SKILLS</h3>
      {skillGroups.map((group, groupIndex) => (
        <div key={groupIndex}>
          <h4>{group.group}</h4>
          <div className='skill-group'>
            {group.items.map((skill, skillIndex) => (
              <div key={skillIndex} className='skill-item'>
                <FontAwesomeIcon icon={skill.icon} size="2x" color={skill.color} />
                <span>{skill.name}</span>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default Skills;
