import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import Axios or another HTTP client

import DataProjects from './components/DataProjects';
import BusinessProcessProjects from './components/BusinessProcessProjects';
import TechnicalProjects from './components/TechnicalProjects';
import ProjectSortFilter from './components/PorjectSortFilter';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [sortKey, setSortKey] = useState('name');
  const [filterCategory, setFilterCategory] = useState('all');

  // Define a function to fetch projects from your API
  const fetchProjects = () => {
    axios.get('/api/projectsWithCategories') // Use the /projectsWithCategories route to get projects with categories
      .then((response) => {
        setProjects(response.data);
      })
      .catch((error) => {
        console.error('Error retrieving projects:', error);
      });
  };

  // Fetch projects with categories when the component mounts
  useEffect(() => {
    fetchProjects();
  }, []);

  const handleSortChange = (key) => {
    setSortKey(key);
  };

  const handleFilterChange = (category) => {
    setFilterCategory(category);
  };

  // Filter and sort projects
  const filteredProjects = projects.filter((project) => {
    if (filterCategory === 'all' || project.category === filterCategory) {
      return true;
    }
    return false;
  });

  const sortedProjects = filteredProjects.slice().sort((a, b) => {
    if (sortKey === 'name') {
      return a.name.localeCompare(b.name);
    } else if (sortKey === 'date') {
      return new Date(b.date) - new Date(a.date);
    }
    return 0;
  });

  return (
    <div className="portfolio">
      <h1>My Portfolio</h1>

      <ProjectSortFilter onSortChange={handleSortChange} onFilterChange={handleFilterChange} />

      <DataProjects projects={sortedProjects.filter((project) => project.category === 'data')} />
      <BusinessProcessProjects projects={sortedProjects.filter((project) => project.category === 'business')} />
      <TechnicalProjects projects={sortedProjects.filter((project) => project.category === 'technical')} />
    </div>
  );
}

export default Portfolio;
