import React, { useState } from 'react';
import CategoryName from './components/CategoryName';

const ProjectCard = ({ project }) => {
  const [expanded, setExpanded] = useState(false);

  return (
    <div className={`project-card ${expanded ? 'expanded' : ''}`} onClick={() => setExpanded(!expanded)}>
      <img src={project.ImageLink} alt={project.Name} />
      <h3>{project.Name}</h3>
      <p>{project.Description}</p>

      {expanded && (
        <div className="details">
          <p>Date: {project.Date}</p>
          <p>Achievements: {project.Achievements}</p>
          <p>Links: {project.Links}</p>
          <p>Category: <CategoryName categoryId={project.CategoryId} /></p>
          {/* Add other project details here */}
        </div>
      )}
    </div>
  );
};

export default ProjectCard;
