import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProjectDetails({ projectId }) {
  const [project, setProject] = useState(null);
  const [categoryName, setCategoryName] = useState('');

  useEffect(() => {
    // Fetch project details based on projectId
    axios
      .get(`${apiUrl}/api/projects/${projectId}`)
      .then((response) => {
        setProject(response.data);

        // Once you have the project details, fetch the category name based on the project's CategoryId
        const categoryId = response.data.CategoryId;
        axios
          .get(`${apiUrl}/api/categories/${categoryId}`)
          .then((categoryResponse) => {
            setCategoryName(categoryResponse.data.Name);
          })
          .catch((categoryError) => {
            console.error('Error retrieving category name:', categoryError);
          });
      })
      .catch((error) => {
        console.error('Error retrieving project details:', error);
      });
  }, [projectId]);

  if (!project) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{project.Name}</h2>
      <p>{project.Description}</p>
      <p>{project.Achievements}</p>
      <p>{project.Date}</p>
      <p>{project.Links}</p>
      <p>{project.ImageLink}</p>
      <p>Category: {categoryName}</p>
      {/* Add other project details here */}
    </div>
  );
}

export default ProjectDetails;
