import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectCard from './components/ProjectCard';
import CategoryName from './components/CategoryName';
import './portfolio.css';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const apiUrl = process.env.REACT_APP_API_URL;
  const [categoryNames, setCategoryNames] = useState({});
  const [currentCategoryIndex, setCurrentCategoryIndex] = useState(0);

  useEffect(() => {
    axios
      .get(`${apiUrl}/api/projects`)
      .then((response) => {
        setProjects(response.data);
      })
      .catch((error) => {
        console.error('Error retrieving projects:', error);
      });

    axios
      .get(`${apiUrl}/api/categories`)
      .then((response) => {
        const categoryData = {};
        response.data.forEach((category) => {
          categoryData[category.Id] = category.Name;
        });
        setCategoryNames(categoryData);
      })
      .catch((error) => {
        console.error('Error retrieving categories:', error);
      });
  }, []);

  const categories = Object.keys(categoryNames);

  const handlePreviousCategory = () => {
    if (currentCategoryIndex > 0) {
      setCurrentCategoryIndex(currentCategoryIndex - 1);
    }
  };

  const handleNextCategory = () => {
    if (currentCategoryIndex < categories.length - 1) {
      setCurrentCategoryIndex(currentCategoryIndex + 1);
    }
  };

  return (
    <div className="portfolio">
      <div className="category-container">
        <h2>
          (CategoryName Id={categories[currentCategoryIndex]} Projects)
        </h2>
        <div className="project-list">
          {projects
            .filter((project) => project.CategoryId === categories[currentCategoryIndex])
            .map((project) => (
              <ProjectCard key={project.Id} project={project} />
            ))}
        </div>
        <div className="navigation-buttons">
          <button onClick={handlePreviousCategory}>Previous</button>
          <button onClick={handleNextCategory}>Next</button>
        </div>
      </div>
    </div>
  );
}

export default Portfolio;
