import React, { useState } from 'react';

function Contact() {
  const [formData, setFormData] = useState({
    name: '',
    subject: '',
    text: '',
    file: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFormData({ ...formData, file });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Prepare the form data to be sent to the server
    const data = new FormData();
    data.append('name', formData.name);
    data.append('subject', formData.subject);
    data.append('text', formData.text);
    data.append('file', formData.file);

    // Make an API request to your server to handle the email sending
    // Replace 'YOUR_API_ENDPOINT' with the actual server endpoint
    fetch('YOUR_API_ENDPOINT', {
      method: 'POST',
      body: data,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Email sent:', data);
        // You can add code here to show a success message to the user
      })
      .catch((error) => {
        console.error('Error sending email:', error);
        // Handle and display an error message to the user
      });
  };

  return (
    <div>
      <h1>Contact Us</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>
        <br />
        <label>
          Subject:
          <input type="text" name="subject" value={formData.subject} onChange={handleChange} required />
        </label>
        <br />
        <label>
          Message:
          <textarea name="text" value={formData.text} onChange={handleChange} required />
        </label>
        <br />
        <label>
          Attach File:
          <input type="file" name="file" onChange={handleFileChange} />
        </label>
        <br />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default Contact;
