import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectCard from './components/ProjectCard';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [categoryNames, setCategoryNames] = useState({});

  const apiUrl = process.env.REACT_APP_API_URL;

  useEffect(() => {
    axios
      .get(`${apiUrl}/api/projects`)
      .then((response) => {
        setProjects(response.data);
      })
      .catch((error) => {
        console.error('Error retrieving projects:', error);
      });
  }, []);

  // Group projects by category
  const projectsByCategory = projects.reduce((result, project) => {
    if (!result[project.CategoryId]) {
      result[project.CategoryId] = [];
    }
    result[project.CategoryId].push(project);
    return result;
  }, {});

  return (
    <div className="portfolio">
      <h1>My Portfolio</h1>
      {Object.keys(projectsByCategory).map((categoryId) => (
        <div key={categoryId}>
          <h2>Category {categoryNames[categoryId]}</h2>
          {projectsByCategory[categoryId].map((project) => (
            <ProjectCard key={project.Id} project={project} />
          ))}
        </div>
      ))}
    </div>
  );
}

export default Portfolio;
