import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectSortFilter from './components/ProjectSortFilter';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [categories, setCategories] = useState([]);
  const [sortKey, setSortKey] = useState('name');
  const [filterCategory, setFilterCategory] = useState('all');

  const fetchProjects = () => {
    axios.get(filterCategory === 'all' ? '/api/projects' : '/api/projectsWithCategories')
      .then((response) => {
        setProjects(response.data);
      })
      .catch((error) => {
        console.error('Error retrieving projects:', error);
      });
  };

  const fetchCategories = () => {
    axios.get('/api/categories')
      .then((response) => {
        setCategories(response.data);
      })
      .catch((error) => {
        console.error('Error retrieving categories:', error);
      });
  };

  useEffect(() => {
    fetchProjects();
    fetchCategories();
  }, [filterCategory]); // Refetch projects when the filter category changes

  const handleSortChange = (key) => {
    setSortKey(key);
  };

  const handleFilterChange = (category) => {
    setFilterCategory(category);
  };

  // Filter and sort projects
  const filteredProjects = projects.filter((project) => {
    if (filterCategory === 'all' || project.category === filterCategory) {
      return true;
    }
    return false;
  });

  const sortedProjects = filteredProjects.slice().sort((a, b) => {
    if (sortKey === 'name') {
      return a.name.localeCompare(b.name);
    } else if (sortKey === 'date') {
      return new Date(b.date) - new Date(a.date);
    }
    return 0;
  });

  return (
    <div className="portfolio">
      <h1>My Portfolio</h1>

      {/* Pass categories to the ProjectSortFilter component */}
      <ProjectSortFilter onSortChange={handleSortChange} onFilterChange={handleFilterChange} categories={categories} />

    </div>
  );
}

export default Portfolio;
