import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectCard from './components/ProjectCard';
import CategoryName from './components/CategoryName';
import './portfolio.css';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const projectsPerPage = 3; // Number of projects to display per page

  const apiUrl = process.env.REACT_APP_API_URL;

  useEffect(() => {
    axios
      .get(`${apiUrl}/api/projects`)
      .then((response) => {
        setProjects(response.data);
      })
      .catch((error) => {
        console.error('Error retrieving projects:', error);
      });
  }, []);

  const groupProjectsByCategory = () => {
    const groupedProjects = {};

    projects.forEach((project) => {
      if (!groupedProjects[project.CategoryId]) {
        groupedProjects[project.CategoryId] = [];
      }
      groupedProjects[project.CategoryId].push(project);
    });

    return groupedProjects;
  };

  const renderProjects = () => {
    const groupedProjects = groupProjectsByCategory();
    const categoryIds = Object.keys(groupedProjects);
    const startIndex = currentPage * projectsPerPage;
    const endIndex = startIndex + projectsPerPage;

    return categoryIds.map((categoryId) => (
      <div key={categoryId} className="category-container">
        <h2> <CategoryName Id={categoryId} /> Projects</h2>
        <div className="project-list">
          {groupedProjects[categoryId].slice(startIndex, endIndex).map((project) => (
            <ProjectCard key={project.Id} project={project} />
          ))}
        </div>
      </div>
    ));
  };

  const handleNextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  const handlePreviousPage = () => {
    setCurrentPage(currentPage - 1);
  };

  return (
    <div className="portfolio">
      {renderProjects()}
      <div className="pagination">
        {currentPage > 0 && (
          <button onClick={handlePreviousPage}>Previous Page</button>
        )}
        {projects.length > (currentPage + 1) * projectsPerPage && (
          <button onClick={handleNextPage}>Next Page</button>
        )}
      </div>
    </div>
  );
}

export default Portfolio;
