import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectCard from './components/ProjectCard';
import CategoryName from './components/CategoryName';
import './portfolio.css';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [currentGroup, setCurrentGroup] = useState(0); // Current group index
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0); // Current project index within the group
  const apiUrl = process.env.REACT_APP_API_URL;
  const [categoryNames, setCategoryNames] = useState({});

  useEffect(() => {
    axios
      .get(`${apiUrl}/api/projects`)
      .then((response) => {
        setProjects(response.data);
      })
      .catch((error) => {
        console.error('Error retrieving projects:', error);
      });

    axios
      .get(`${apiUrl}/api/categories`)
      .then((response) => {
        const categoryData = {};
        response.data.forEach((category) => {
          categoryData[category.Id] = category.Name;
        });
        setCategoryNames(categoryData);
      })
      .catch((error) => {
        console.error('Error retrieving categories:', error);
      });
  }, []);

  const groupProjectsByCategory = () => {
    const groupedProjects = {};

    projects.forEach((project) => {
      if (!groupedProjects[project.CategoryId]) {
        groupedProjects[project.CategoryId] = [];
      }
      groupedProjects[project.CategoryId].push(project);
    });

    return groupedProjects;
  };

  const handleNavigateNext = () => {
    const groupedProjects = groupProjectsByCategory();
    if (
      groupedProjects[currentGroup] &&
      currentProjectIndex < groupedProjects[currentGroup].length - 1
    ) {
      setCurrentProjectIndex(currentProjectIndex + 1);
    } else if (
      currentGroup < Object.keys(groupedProjects).length - 1 &&
      groupedProjects[currentGroup + 1] &&
      groupedProjects[currentGroup + 1].length > 0
    ) {
      setCurrentGroup(currentGroup + 1);
      setCurrentProjectIndex(0);
    }
  };


  const handleNavigatePrevious = () => {
    if (currentProjectIndex > 0) {
      setCurrentProjectIndex(currentProjectIndex - 1);
    } else if (currentGroup > 0) {
      const groupedProjects = groupProjectsByCategory();
      setCurrentGroup(currentGroup - 1);
      setCurrentProjectIndex(groupedProjects[currentGroup - 1].length - 1);
    }
  };

  const groupedProjects = groupProjectsByCategory();

  return (
    <div className="portfolio">
      <div className="navigation">
        <div className="nav-button" onClick={handleNavigatePrevious}>
          &lt; Previous
        </div>
        <div className="nav-button" onClick={handleNavigateNext}>
          Next &gt;
        </div>
      </div>
      {groupedProjects[currentGroup] && (
        <ProjectCard
          project={groupedProjects[currentGroup][currentProjectIndex]}
          onNavigateNext={handleNavigateNext}
          onNavigatePrevious={handleNavigatePrevious}
        />
      )}
    </div>
  );
}

export default Portfolio;
